import { useReducer } from "react";
import DigitButton from "./DigitButton";
import "./style.css";

// function preformed in state
// action = {type, payload}
// different types of actions and different actions
// actions pass along some parameters

// create global variable for actions 
export const ACTIONS = {
  ADD_DIGIT: 'add-digit',
  CHOOSE_OPERATION:'choose-operation',
  CLEAR: 'clear',
  DELETE_DIGIT: 'delete-digit',
  EVALUETE: 'evaluate'
}

function reducer(state, {type, payload}){
  switch(type){
    // if no currentOperand create new digit or show empty
    case ACTIONS.ADD_DIGIT:

      return {...state,

        currentOperand : `${state.currentOperand || ""} ${payload.digit}`,}
  }
}

function App() {
  // use reducers
  // convert current state to a new version of the state 
  // based on an action sended
  // {previousOperand, currentOperand, operation} = state
  const [{previousOperand, currentOperand, operation}, dispatch] = useReducer(reducer,{})

  // dispatch({ type: ACTIONS.ADD_DIGIT, payload: {digit:1}})

  return (
    <div className="calculator-grid">
      <div className="output">
        {/* state variables put in*/}
        <div className="previous-operand">{previousOperand}{operation}</div>
        <div className="current-operand">{currentOperand}</div>
      </div>
      <button className="span-two">AC</button>
      <button>DEL</button>
      <DigitButton digit='รท' dispatch={dispatch}/>
      <button>รท</button>
      <DigitButton digit='1' dispatch={dispatch}/>
      <button>2</button>
      <button>3</button>
      <button>*</button>
      <button>4</button>
      <button>5</button>
      <button>6</button>
      <button>+</button>
      <button>7</button>
      <button>8</button>
      <button>9</button>
      <button>-</button>
      <button>.</button>
      <button>0</button>
      <button className="span-two">=</button>
    </div>
  );
}

export default App;
